import { useState, useEffect } from "react";
import ReactPaginate from "react-paginate";

export const PostList = () => {
  const [data, setData] = useState();

  useEffect(() => {
    // fetch data
    const dataFetch = async () => {
      const data = await (
        await fetch("https://jsonplaceholder.typicode.com/posts")
      ).json();
      // set state when the data received
      setData(data);
    };

    dataFetch();
  }, []);
  return data && <Layout postList={data} />;
};

export const Layout = ({ postList }) => {
  const [pagination, setPagination] = useState({
    data: postList,
    offset: 0,
    numberPerPage: 4,
    pageCount: 0,
    currentData: [],
  });
  useEffect(() => {
    setPagination((prevState) => ({
      ...prevState,
      pageCount: prevState.data.length / prevState.numberPerPage,
      currentData: prevState.data.slice(
        pagination.offset,
        pagination.offset + pagination.numberPerPage
      ),
    }));
  }, [pagination.numberPerPage, pagination.offset]);
  const handlePageClick = (event) => {
    const selected = event.selected;
    const offset = selected * pagination.numberPerPage;
    setPagination({ ...pagination, offset });
  };
  return (
    <div>
      <div className="container">
        <div className="row row-cols-1 row-cols-sm-2 row-cols-lg-4 ">
          {pagination.currentData &&
            pagination.currentData.map((post, index) => (
              <div className="col" key={post.id}>
                <Card post={post} key={`${index}-${post.userId}`} />
              </div>
            ))}
        </div>
      </div>
      <ReactPaginate
        previousLabel={"previous"}
        nextLabel={"next"}
        breakLabel={"..."}
        pageCount={pagination.pageCount}
        marginPagesDisplayed={2}
        pageRangeDisplayed={5}
        onPageChange={handlePageClick}
        containerClassName={"pagination"}
        activeClassName={"active"}
      />
    </div>
  );
};

export const Card = ({ post }) => (
  <div className="card p-3 mt-3 fs-6 text-start d-flex" key={post.id}>
    <div className="card-header">
      <h6>
        <span className="badge border border-success text-success ">
          User: {post.userId}
        </span>
      </h6>
      <h5 className="card-title cap">{post.title}</h5>
    </div>
    <div className="card-body">
      <p className="card-text cap">{post.body}</p>
      <a href="#" className="btn btn-primary">
        See more...
      </a>
    </div>
  </div>
);
