import Layout from "../layouts/Layout";
import { useLoaderData } from "react-router-dom";

const PostList = () => {
  const posts = useLoaderData();
  //console.log("posts=", posts);
  return <Layout list={posts} />;
};

export default PostList;

export async function postListLoader({ request, params }) {
  const postListResponse = await fetch(
    "https://jsonplaceholder.typicode.com/posts"
  );
  if (!postListResponse) {
    throw new Response(JSON.stringify({ message: "Could not fetch" }), {
      status: 500,
    });
  } else {
    const postListResData = await postListResponse.json();
  }

  const commentsResponse = await fetch(
    "https://jsonplaceholder.typicode.com/posts"
  );
  if (!commentsResponse) {
    throw new Response(JSON.stringify({ message: "Could not fetch" }), {
      status: 500,
    });
  } else {
    const commentsResData = await commentsResponse.json();
  }

  return [postListResData, commentsResData];
}
